import React, { Component } from 'react';
import axios from 'axios';
import "./style.css";

class TaxBrackets extends Component{
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = { 
            tax_bracket_count: '',
        }
      }
        handleClick(event) {
            event.preventDefault()
            
            const bracketObject = {
                tax_bracket_count: event.target.id
            }

            axios.post('http://localhost:3000/BudgetVoting', bracketObject)
                .then((res) => {
                    console.log(res.data)
                }).catch((error) =>{
                    console.log(error)
                });

            this.setState({ tax_bracket_count:' '});
        }
        
    render(){
    return(
        <div class="text-center">
            <h1 class="tax-title">Select Your Tax Bracket to Vote!</h1>
            <button type="button" class="btn btn-primary btn-lg tax" value={this.state.tax_bracket_count} onClick={this.handleClick} id="0">Up to $19,750</button>
            <button type="button" class="btn btn-light btn-lg tax" value={this.state.tax_bracket_count} onClick={this.handleClick} id="1">$19,751 to $80,250</button>
            <button type="button" class="btn btn-danger btn-lg tax" value={this.state.tax_bracket_count} onClick={this.handleClick} id="2">$80,251 to $171,050</button>
            <button type="button" class="btn btn-primary btn-lg tax" value={this.state.tax_bracket_count} onClick={this.handleClick} id="3">$171,051 to $326,600</button>
            <button type="button" class="btn btn-light btn-lg tax" value={this.state.tax_bracket_count} onClick={this.handleClick} id="4">$326,601 to $414,700</button>
            <button type="button" class="btn btn-danger btn-lg tax" value={this.state.tax_bracket_count} onClick={this.handleClick} id="5">$414,701 to $622,050</button>
            <button type="button" class="btn btn-primary btn-lg tax" value={this.state.tax_bracket_count} onClick={this.handleClick} id="6">Over $622,050</button>
        </div>
        
    )
}
}

export default TaxBrackets;
