import React from "react";
import { Doughnut } from "react-chartjs-2";
import "./suggestedBudgets.css";
import axios from "axios";
import DeptDropdown from "../DeptDropdown/DeptDropdown";


export default class UnweightedBudget extends React.Component {
  state = {
    datasets: [{
      data: [1, 1, 1],
      // data: [7, 10, 20, 20, 30, 30, 10, 7, 10, 20, 20, 30, 30, 10, 7, 10, 20, 20, 30],
      backgroundColor: [
        "#124E73",
        "#176FA6",
        "#11A8BD",
        "#10B39A",
        "#08C976",

        "#F9F03E",
        "#F2C849",
        "#F2B138",
        "#F9A23E",
        "#F29829",

        "#124E73",
        "#176FA6",
        "#11A8BD",
        "#10B39A",
        "#08C976",

        "#F9F03E",
        "#F2C849",
        "#F2B138",
        "#F9A23E",
        "#F29829"
      ],
    }],
    borderColor: [
      'rgba(57, 249, 202)'
    ],
    borderWidth: 1,
    labels: ['Dept of State', 'Dept of Defense', 'Dept of Energy', 'Dept of Labor', 'Dept of Agriculture', 'Dept of Homeland Security', 'Dept of Transportation', 'Dept of Commerce', 'Dept of Education', 'Dept of Health & Human Services', 'Dept of Housing & Urban Development', 'Dept of Justice', 'Dept of the Interior', 'Dept of the Treasury', 'Environmental Protection Agency', 'Federal Communication Comission', 'Federal Election Comission', 'Federal Trade Comission', 'Equal Employment Opportunity Comission']
  }

  componentDidMount() {
    axios.get('/api/vote/get')
      .then(res => {
        const votes = res.data;
        console.log(res);
        this.setState({ votes });
        console.log(votes);
        console.log(votes.length);

        //agriculture data manipulation

        for (var i = 0; i < votes.length; i++) {

        }




        //data manipulation piece

        //defines each dept as an array
        const agriculture = [];
        const commerce = [];
        const communicate = [];
        const defense = [];
        const education = [];
        const election = [];
        const energy = [];
        const envrionmental_protection = [];
        const equal_employment = [];
        const health_human_services = [];
        const homeland_security = [];
        const housing_urban_development = [];
        const interior = [];
        const justice = [];
        const labor = [];
        const nasa = [];
        const social = [];
        const state = [];
        const trade = [];
        const transportation = [];
        const treasury = [];
        const veterans_affairs = [];

        //for loop that grabs each of the values of depts
        for (var i = 0; i < votes.length; i++) {

          //pushes the dept values to arrays
          agriculture.push(votes[i].agriculture);
          commerce.push(votes[i].commerce);
          communicate.push(votes[i].communication)
          defense.push(votes[i].defense)
          education.push(votes[i].education)
          election.push(votes[i].election)
          energy.push(votes[i].energy)
        }

        //totals the dept values 
        const agTotal = agriculture.reduce((a, b) => a + b, 0)
        const commerceTotal = commerce.reduce((a, b) => a + b, 0)
        const communicateTotal = communicate.reduce((a, b) => a + b, 0)
        const defenseTotal = defense.reduce((a, b) => a + b, 0)
        const educationTotal = education.reduce((a, b) => a + b, 0)
        const electionTotal = election.reduce((a, b) => a + b, 0)
        const energyTotal = energy.reduce((a, b) => a + b, 0)

        //averages out the dept totals based on vote counts
        const agAvg = agTotal / votes.length
        const commerceAvg = commerceTotal / votes.length
        const communicateAvg = communicateTotal / votes.length
        const defenseAvg = defenseTotal / votes.length
        const educationAvg = educationTotal / votes.length
        const electionAvg = electionTotal / votes.length
        const energyAvg = energyTotal / votes.length

        //pushing arrays to state so they can be used in our chart
        const newArr = [agAvg, commerceAvg, communicateAvg, defenseAvg, educationAvg, electionAvg, energyAvg];

        //updates the state with our new array of data
        this.setState({
          data: newArr
        })

      })
  }

  render() {
    console.log(this.state);
    return (
      <div className="suggestedSpending-unweighted">
        <Doughnut
          data={this.state}
          options={{
            title: {
              display: true,
              text: 'Unweighted Budget',
              fontSize: 20
            },
            legend: {
              display: true,
              position: 'right'
            },
            tooltips: {
              intersect: false,
            },

            animation: {
              animateScale: true,
              animateRotate: true
            },
            layout: {
              padding: {
                left: 50,
                right: 0,
                top: 0,
                bottom: 0
              }
            }
          }}
        />
      </div>
    );
  }
}
