import React from "react";
import "../assets/styles/home.css";
import Jumbotron from "../components/Jumbotron/Jumbotron.js";
import BarChart from "../components/BarChartRace/BarChart.js";
import data from '../components/BarChartRace/Data';
import NonProfitBanner from '../components/NonProfitBanner/NonProfitBanner';

const Colors = () => {
  const colorOptions = ["#8c1127", "#d92546", "#071a40", "#2a558c", "#5b9ed9"];
  const colorChoice = Math.floor(Math.random() * 4) + 1;
  return colorOptions[colorChoice];
}

const len = data[Object.keys(data)[0]].length;
const keys = Object.keys(data);

const colors = keys.reduce((res, item) => ({
  ...res,
  ...{ [item]: Colors() }
}), {});

const labels = keys.reduce((res, item, idx) => {
  return {
    ...res,
    ...{
      [item]: (
        <div className="itemLabel">
          <div>{item}</div>
        </div>
      )
    }
  }
}, {});

const time = Array(20).fill(0).map((itm, idx) => idx + 1);

const Home = () => {
  return (
    <main>
      <Jumbotron />

      <h3>Historical Government Spending by Agency</h3>
      <div className="barChartRace">
        <BarChart
          start={true}
          data={data}
          timeline={time}
          labels={labels}
          colors={colors}
          len={len}
          timeout={400}
          delay={100}
          timelineStyle={{
            textAlign: "center",
            fontSize: "50px",
            color: "rgb(148, 148, 148)"
          }}
          textBoxStyle={{
            textAlign: "right",
            color: "rgb(133, 131, 131)",
            fontSize: "30px",
          }}
          barStyle={{
            height: "25px",
            marginTop: "2px",
            borderRadius: "10px",
          }}
          width={[15, 75, 10, 45, 60]}
          maxItems={10}
        />
      </div>

      <h3>Total Federal Spending by Agency 2019</h3>
      <NonProfitBanner />
    </main >
  );
};

export default Home;
